import React, { Component } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import axios from 'axios';


class FormEditProduk extends Component {

constructor() {
        super();
        this.state = {
          post:[]
        }
    }

componentDidMount(){
  axios.get('http://localhost:3004/posts/1575063907508')
  .then((result) => {
      console.log(result.post);
      this.setState({
        post: result.data
      })
  })


  // fetch('https://jsonplaceholder.typicode.com/posts')
  // .then(response => response.json())
  // .then(json => console.log(json))
}

// handleChange(event) {

// let name = event.target.name;
//     this.setState({
//       name: event.target.value
//     });
// }

render(){
    return (
    <MDBContainer>
      <MDBRow>
        <MDBCol>
          <form onSubmit={this.props.submit}>
            <p className="h4 text-center mb-4">Edit Data Produk</p>

            <hr/>

            <label htmlFor="defaultFormRegisterNameEx" className="grey-text">
              Nama Produk
            </label>
            <input
              type="text"
              id="nama_produk"
              name="nama_produk"
              className="form-control"
              value={this.state.post.nama_produk}
              onChange={this.handleChange}
            />
            <br />
            <label htmlFor="defaultFormRegisterEmailEx" className="grey-text">
              Harga Produk
            </label>
            <input
              type="text"
              id="harga_produk"
              name="harga_produk"
              className="form-control"
              value={this.state.post.harga_produk}
              onChange={this.handleChange}
            />
            <br />
            <label
              htmlFor="defaultFormRegisterConfirmEx"
              className="grey-text"
            >
              Url Produk
            </label>
            <input
              type="text"
              id="image_produk"
              name="image_produk"
              className="form-control"
              value={this.state.post.image_produk}
              onChange={this.handleChange}
            />
            <br />
            <div className="text-center mt-4">
              <MDBBtn color="unique" type="submit">
                Tambah Produk
              </MDBBtn>
            </div>
          </form>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
    );
  };
}

export default FormEditProduk;
